package com.mycopany.java_Study.ch04;

public class ExWhile1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// ch04.09
		
		//1부터 10까지 더하는 프로그램
		int i=1;
		int sum = 0;
		while(i<=10) {
			  sum +=i;
			  i++;
			  }
		System.out.println(sum);
		
		
		
		
	}

}
